# pylint: disable=C0115:missing-class-docstring,C0114:missing-module-docstring,C0301:line-too-long# pylint: disable=W0612:unused-variableimport osfrom dotenv import load_dotenvclass EnvironmentVariableService:    def __init__(self):        self.variables=load_dotenv()    @staticmethod    def get(environment_varible_name):        '''        retrieves environment variable by name        '''        return os.getenv(environment_varible_name)
